import React, { useState } from 'react';
import '../ComponentStyles.css';

const CPUView = ({ cpuState }) => {
  return (
    <div className="cpu-view">
      <h3>CPU State</h3>
      <div>
        <p>Acumulador: {cpuState.accumulator}</p>
        <p>Reg. Entrada: {cpuState.inRegister}</p>
        <p>Cont. Programa: {cpuState.programCounter}</p>
        <p>Reg. Instrucciones: {cpuState.instructionsRegister}</p>
      </div>
    </div>
  );
};

export default CPUView;
