import React, { useState, useEffect } from 'react';
import ControlPanel from './components/ControlPanel';
import CPUView from './components/CPUView';
import MemoryView from './components/MemoryView';
import UnidadDeControl from './components/UnidadDeControl';
import Arrow from './components/Arrow';  
import './App.css';

function App() {
  const [cpuState, setCpuState] = useState({});
  const [memoryState, setMemoryState] = useState([]);
  const [isPaused, setIsPaused] = useState(false);

  // Función para cargar el programa en la memoria
  const loadProgram = async (program) => {
    try {
      const response = await fetch('http://localhost:3001/api/fill-memory', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(program),
      });
      const data = await response.json();
      console.log('Memoria actualizada:', data.memory);
      setMemoryState(data.memory);
    } catch (error) {
      console.error('Error al cargar el programa:', error);
    }
  };

  // Función para ejecutar un paso del ciclo de la CPU
  const executeStep = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/run-step-by-step', {
        method: 'GET',
      });
      const data = await response.json();
      console.log('Estado de la CPU:', data);
      setCpuState(data.registers);
    } catch (error) {
      console.error('Error al ejecutar el paso:', error);
    }
  };

  // Obtener el estado de la memoria
  useEffect(() => {
    const fetchMemory = async () => {
      try {
        const response = await fetch('http://localhost:3001/api/memory-status');
        const data = await response.json();
        setMemoryState(data.memory);
      } catch (error) {
        console.error('Error al obtener el estado de la memoria:', error);
      }
    };

    fetchMemory();
  }, []);

  // Obtener el estado de la CPU (puedes hacer esto al montar el componente o después de cada acción)
  useEffect(() => {
    const fetchCpuState = async () => {
      try {
        const response = await fetch('http://localhost:3001/api/cpu-status');
        const data = await response.json();
        setCpuState(data);
      } catch (error) {
        console.error('Error al obtener el estado de la CPU:', error);
      }
    };

    fetchCpuState();
  }, []);

  return (
    <div className="App">
      <h1>Von Neumann Simulator</h1>
      <div className="container">
        <div className="ControlPanel">
          <ControlPanel 
            loadProgram={loadProgram} 
            executeStep={executeStep} 
          />
        </div>
        <div className="views">
          <div className="top-row">
            <div className="view-container">
              <UnidadDeControl cpuState={cpuState} />
            </div>
            <div className="view-container">
              <CPUView cpuState={cpuState} />
            </div>
          </div>
          <div className="view-container">
            <MemoryView memoryState={memoryState} />
          </div>

          {/* Arrows for visuals */}
          <Arrow x1={550} y1={230} x2={550} y2={330} />
          <Arrow x1={580} y1={342} x2={700} y2={240} />
          <Arrow x1={305} y1={230} x2={305} y2={330} />
          <Arrow x1={185} y1={230} x2={270} y2={330} />
        </div>
      </div>
    </div>
  );
}

export default App;
