import React, { useState } from 'react';
import '../ComponentStyles.css';

function ControlPanel({ loadProgram, executeStep, pauseCycle, resumeCycle, resetCycle }) {
  const [minuendo, setMinuendo] = useState('');
  const [sustraendo, setSustraendo] = useState('');
  const [error, setError] = useState('');
  const [operation, setOperation] = useState('Suma');
  const [isPaused, setIsPaused] = useState(false);
  const [options] = useState([
    { label: 'Suma', value: 'Suma' },
    { label: 'Resta', value: 'Resta' },
    { label: 'Multiplicación', value: 'Multiplicación' },
    { label: 'Exponente', value: 'Exponente' },
  ]);

  const handleMinuendoChange = (event) => {
    setMinuendo(event.target.value);
  };

  const handleSustraendoChange = (event) => {
    setSustraendo(event.target.value);
  };

  const handleOperationChange = (event) => {
    setOperation(event.target.value);
  };

  const validateInput = () => {
    if (parseInt(minuendo) > 255 || parseInt(sustraendo) > 255) {
      setError('Los valores no deben superar los 255');
      return false;
    }
    setError('');
    return true;
  };

  const handleLoadProgram = () => {
    if (!validateInput()) return;
    const program = {
      operation,
      dato1: parseInt(minuendo),
      dato2: parseInt(sustraendo)
    };
    loadProgram(program);
  };

  const handleExecuteStep = () => {
    if (!validateInput()) return;
    executeStep();
  };

  const handlePause = () => {
    setIsPaused(true);
    pauseCycle();
  };

  const handleResume = () => {
    setIsPaused(false);
    resumeCycle();
  };

  const handleReset = () => {
    resetCycle();  // Reiniciar el ciclo
  };

  return (
    <div>
      <div>
        <textarea
          rows="1"
          cols="10"
          placeholder="Ingrese número"
          value={minuendo}
          onChange={handleMinuendoChange}
        />
      </div>
      <div>
        <textarea
          rows="1"
          cols="10"
          placeholder="Ingrese número"
          value={sustraendo}
          onChange={handleSustraendoChange}
        />
      </div>
      <div>
        <label htmlFor="operaciones" className="text-center">Seleccione la operación:</label>
        <select id="sel" required onChange={handleOperationChange}>
          {options.map((option, index) => (
            <option key={index} value={option.value}>{option.label}</option>
          ))}
        </select>
      </div>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <div>
        <button onClick={handleLoadProgram}>Comenzar Ciclo</button>
        <button onClick={handleExecuteStep}>Paso a paso</button>
        <button onClick={handlePause}>Pausar</button>
        <button onClick={handleResume}>Reanudar</button>
        <button onClick={handleReset}>Reiniciar</button> {/* Botón para reiniciar */}
      </div>
    </div>
  );
}

export default ControlPanel;
