import React, { useState } from 'react';
import '../ComponentStyles.css';

function ControlPanel({ loadProgram, executeStep }) {
  const [minuendo, setMinuendo] = useState('');
  const [sustraendo, setSustraendo] = useState('');
  const [resultado, setResultado] = useState(null);
  const [isPaused, setIsPaused] = useState(false); // State to manage pause/resume
  const [error, setError] = useState(''); // State to manage error messages
  const [options] = useState([
    { label: 'Suma', value: 'Suma' },
    { label: 'Resta', value: 'Resta' },
    { label: 'Multiplicación', value: 'Multiplicación' },
    { label: 'Exponente', value: 'Exponente' },
  ]);

  const handleMinuendoChange = (event) => {
    setMinuendo(event.target.value);
  };

  const handleSustraendoChange = (event) => {
    setSustraendo(event.target.value);
  };

  // Validate input and show error if values are greater than 255
  const validateInput = () => {
    if (parseInt(minuendo) > 255 || parseInt(sustraendo) > 255) {
      setError('Los valores no deben superar los 255');
      return false;
    }
    setError('');
    return true;
  };

  const handleLoadProgram = () => {
    if (!validateInput()) return; // Prevent loading if validation fails

    const exampleProgram = [
      { instruction: 'LOAD', address: 1 },
      { instruction: 'ADD', address: 2 },
      { instruction: 'STORE', address: 3 },
    ];
    loadProgram(exampleProgram);
  };

  const handleExecuteStep = () => {
    if (!validateInput()) return; // Prevent step execution if validation fails

    // Example execution step
    executeStep();
  };

  const handlePause = () => {
    setIsPaused(true);
    // Logic to pause the simulation goes here
  };

  const handleResume = () => {
    setIsPaused(false);
    // Logic to resume the simulation goes here
  };

  return (
    <div>
      <div>
        <textarea
          rows="1"
          cols="10"
          placeholder="Ingrese número"
          value={minuendo}
          onChange={handleMinuendoChange}
        />
      </div>
      <div>
        <textarea
          rows="1"
          cols="10"
          placeholder="Ingrese número"
          value={sustraendo}
          onChange={handleSustraendoChange}
        />
      </div>
      <div>
        <label htmlFor="operaciones" className="text-center">
          Seleccione la operación:
        </label>
        <select id="sel" required>
          {options.map((option, index) => (
            <option key={index} value={option.value}>
              {option.label}
            </option>
          ))}
        </select>
      </div>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <div>
        <button onClick={handleLoadProgram}>Comenzar Ciclo</button>
      </div>
      <div>
        <button onClick={handleExecuteStep}>Paso a paso</button>
      </div>
      <div>
        <button onClick={handlePause}>Pausar</button>
        <button onClick={handleResume}>Reanudar</button>
      </div>
      <div>
        {resultado !== null && <p>Resultado: {resultado}</p>}
      </div>
    </div>
  );
}

export default ControlPanel;
