import React, { useState } from 'react';
import '../ComponentStyles.css';

const UnidadDeControl = () => {
  // Separate state variables for each square
  const [decodificador, setDecodificador] = useState(0);
  const [registroInstrucciones, setRegistroInstrucciones] = useState(0);
  const [contPrograma, setContPrograma] = useState(0);

  return (
    <div className="component-container">
      <h3>Unidad de Control</h3>
      <div className="square">
        <div className="small-squares-container">
          <div className="square-label">
            <div className="small-square">{decodificador}</div>
            <p className="label-text">Decodificador</p>
          </div>
          <div className="square-label">
            <div className="small-square">{registroInstrucciones}</div>
            <p className="label-text">Registro de Instrucciones</p>
          </div>
          <div className="square-label">
            <div className="small-square">{contPrograma}</div>
            <p className="label-text">Cont. Programa</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UnidadDeControl;
