import React, { useState } from 'react';

function ControlPanel({ loadProgram, executeStep }) {

  const [minuendo, setMinuendo] = useState('');
  const [sustraendo, setSustraendo] = useState('');
  const [resultado, setResultado] = useState(null);

  const handleMinuendoChange = (event) => {
    setMinuendo(event.target.value);
  };

  const handleSustraendoChange = (event) => {
    setSustraendo(event.target.value);
  };

  const handleRestar = () => {
    const resultado = parseFloat(minuendo) - parseFloat(sustraendo);
    setResultado(resultado);
  };
  const handleLoadProgram = () => {
    const exampleProgram = [
      { instruction: 'LOAD', address: 1 },
      { instruction: 'ADD', address: 2 },
      { instruction: 'STORE', address: 3 },
    ];
    loadProgram(exampleProgram);
  };
  function createOption(){ // creamos un nodo <option></option> con los valores de los inputs y lo hacemos hijo del select justo al final del resto sus nodos hijos
  let option = document.createElement('option');
  let label = document.createTextNode(`${document.getElementById('input_name').value}`)
  option.value = document.getElementById('input_value').value;
  option.appendChild(label);
  document.getElementById('sel').appendChild(option);
  console.log(option)
}

document.getElementById('create_option').addEventListener('click',createOption) // al clickear el boton llamamos a la funcion

  return (
    <div>
      <div>
        <textarea
          rows="1"
          cols="10"
          placeholder="Ingrese número"
          value={minuendo}
          onChange={handleMinuendoChange}
         />
      </div>
      <div>
        <textarea
          rows="1"
          cols="10"
          placeholder="Ingrese número"
          value={sustraendo}
          onChange={handleSustraendoChange}
        />
      </div>
      <div>
        <label for="operaciones" class="text-center">Seleccione la operación: </label>
        <select id="sel" required>
          <option value="">Seleccionar</option>
          <option value="Suma"> Suma </option>
          <option value="Resta"> Resta </option>
          <option value="Multiplicación"> Multiplicación </option>
          <option value="Exponente"> Exponente </option>
        </select>
      </div>
      <div>
        <button onClick={handleRestar}>Ejecutar</button>
      </div>
      <div>
        <button onClick={handleRestar}>paso a paso</button>
      </div>
      <div>
        {resultado !== null && (
          <p>Resultado de la resta: {resultado}</p>
        )}
      </div>
      <div>
        <button onClick={handleLoadProgram}>Load Program</button>
        <button onClick={executeStep}>Execute Step</button>
      </div>
    </div>
  )
}

export default ControlPanel;
