import React from 'react';
import '../ComponentStyles.css';

const Memory = () => {
  const rows = Array(8).fill(0); // 9 filas para la tabla, inicializadas con 0

  return (
    <div className="component-container">
      <h3>Memory</h3>
      <div className="memory-square">
        {/* Bloque Reg. Direcciones a la izquierda */}
        <div className="memory-left">
          <p>Reg. Direcciones</p>
          <div className="small-square">0</div>
          
        </div>

        {/* Tabla de Memoria */}
          
        <table className="memory-table">   
        <p>Memoria-direccion</p>            
          <tbody> 
               
            {rows.map((_, rowIndex) => (
              <tr key={rowIndex}>
                <td>0</td>
                <td>0</td>
              </tr>
            ))}
          </tbody>
        </table>

        {/* Bloque Reg. Datos a la derecha */}
        <div className="memory-right">
          <p>Reg. Datos</p>
          <div className="small-square">0</div>
        </div>
      </div>
    </div>
  );
};

export default Memory;
